/// <summary>
/// UI builder class for vendor cross reference report.
/// </summary>
class FTDERGenericExecutionUIBuilder extends SysOperationAutomaticUIBuilder
{
}

Method.Source:
    /// <summary>
    /// Manipulate the controls presented to the user depending on whether the user is internal (ACW) or external (vendor).
    /// </summary>
	public void postBuild()
    {
        DialogField formatMapping;

        super();

        formatMapping = this.bindInfo().getDialogField(this.dataContractObject(), methodStr(FTDERGenericExecutionDataContract, parmFormatMappingId));
        formatMapping.allowEdit(false);
    }
